package com.example.themoviedbapp.Retrofit;import static com.example.themoviedbapp.Constants.MoviesConstants.BASE_API;import com.example.themoviedbapp.Model.MoviesModel;import com.example.themoviedbapp.Response.MoviesResponse;import com.example.themoviedbapp.Response.TrailerResponse;import retrofit2.Call;import retrofit2.http.GET;import retrofit2.http.Path;import retrofit2.http.Query;public interface ApiInterface {    @GET("movie/top_rated?api_key=" + BASE_API)    Call<MoviesResponse> getTopRatedMovies();    @GET("movie/popular?api_key=" + BASE_API)    Call<MoviesResponse> getPopMovies();    @GET("trending/movie/day?api_key=" + BASE_API)    Call<MoviesResponse> getTrendingMovies();    @GET("movie/upcoming?api_key=" + BASE_API)    Call<MoviesResponse> getUpcomingMovies();    @GET("trending/tv/day?api_key=" + BASE_API)    Call<MoviesResponse> getTrendingTvShows();    @GET("search/multi?api_key=" + BASE_API)    Call<MoviesResponse> searchMoviesTvShows(@Query("query") String query);    @GET("movie/{id}?api_key=" + BASE_API + "&append_to_response=credits")    Call<MoviesModel> getMovieDetails(@Path("id") int id);    @GET("tv/{id}?api_key=" + BASE_API)    Call<MoviesModel> getTVDetails(@Path("id") int id);    @GET("movie/{id}/videos?api_key=" + BASE_API)    Call<TrailerResponse> getMoviesTrailer(@Path("id") int id);}